function activ(c) {
	allclass();
	if(!$(c).children().hasClass("accl1")) {
		$(c).children().addClass("accl1");
	}
	if(!$(c).children().children().hasClass("circle1")) {
		$(c).children().children().addClass("circle1");
	}
}

function allclass() {
	$(".accl1").each(function() {
		$(this).removeClass("accl1");
		$(this).children().removeClass("circle1");
	})
}

function closeChildren(k) {
	$(".dropdown-menu").each(function(i) {
		if(i != k) {
			$(this).hide();
		}
	});
}
/**
 * 功能:根据$state.go的url系统自动判断并切换显示左侧菜单
 * @param {Object} goUrl
 */
function on_active_menu(goUrl){
	//迭代左侧导航栏中所有的a标签。然后获取每个标签上的ui-sref属性；如果跳转的url与它相等就切换
	$("#navigation").find("a").each(function(i){
		var ref = $(this).attr("ui-sref");
		//相同就切换
		if(ref == goUrl){ 
			allclass();
			//设置选中的高亮
			if(!$(this).hasClass("accl1")) {
				$(this).addClass("accl1");
			}
			if(!$(this).children().hasClass("circle1")) {
				$(this).children().addClass("circle1");
			}
		}
	});
}

$(window).load(function() {
	$("#loader").delay(500).fadeOut(300);
	$(".mask").delay(800).fadeOut(300, function() {
		$("#user-new-messages").addClass("animated bounceIn");
		$("#user-status .my-status").addClass("animated bounceIn")
	})
});