mapper.select:
		select id_ from BIZ_DEBT_GRANT


				and GRANT_CODE = #{cm.grantCode}


				and BUSINESS_TYPES = #{cm.businessTypes}


				and DEBT_CODE = #{cm.debtCode}


				and PROPER_INFO = #{cm.properInfo}


				and CUST_NO = #{cm.custNo}


				and REPAYMENT_MODE = #{cm.repaymentMode}


				and (remark_ like '%'||#{cm.keyword}||'%')


				and GRANT_STATUS = #{cm.grantStatus}


				and ENABLE_ = #{cm.enable}


				and CREATE_BY = #{cm.createBy}


				and INSTITUTION_CODE = #{cm.deptCode}


                and (GRANT_CODE like '%'||#{cm.keyword}||'%')


				and (GRANT_CODE like '%'||#{cm.schemeNum}||'%')


				and (BUSINESS_NAME like '%'||#{cm.businessName}||'%')


				and (PROPOSER like '%'||#{cm.applicant}||'%')


				and (CREATE_TIME >= TO_DATE(#{cm.stateTime}||'00:00:00', 'yyyy-mm-dd hh24:mi:ss'))


				and (CREATE_TIME <= TO_DATE(#{cm.endTime}||'23:59:59', 'yyyy-mm-dd hh24:mi:ss'))


				and HISTORY_STATE is not null


				and HISTORY_STATE = #{cm.historyStatus}


				and GRANT_STATUS not in (9,11,12)

SELECT TO_CHAR(A.ID_) ID_ , A .DEBT_CODE, A .GRANT_CODE, A.SEND_DATE, A.END_DATE, A.BUSINESS_TYPES, A.SCOPE_BUSIN_PERIOD, A.GRACE_PERIOD, A.TOIERANCE_PERIOD, A.IOU_CODE, B.IDENT_NUMBER, B.PROJECT_NAME, B.PROPOSER, B.PROPOSER_NUM, B.BANK_TELL_ID, C.USER_NAME, C.DEPT_CODE, D.DEPT_NAME, E.NAME_ FROM BIZ_DEBT_GRANT A INNER JOIN BIZ_DEBT_MAIN B ON A .DEBT_CODE = B.DEBT_CODE INNER JOIN SYS_USER C ON B.BANK_TELL_ID = C.id_ INNER JOIN SYS_DEPT D ON C.DEPT_CODE = D .ID_ INNER JOIN BIZ_PRODUCT_TYPES E ON A .BUSINESS_TYPES = E .CODE_
and A.GRANT_CODE = #{cm.grantCode}
and A.DEBT_CODE = #{cm.debtCode}
and A.BUSINESS_TYPES = #{cm.businessTypes}

SELECT cust.CUST_NAME_CN, ltype.CREDIT_RATIO, cust.CUST_NO, lines.CREDIT_NO, lines.AMOUNT_TYPE, lines.CREDIT_LINE_NAME FROM BIZ_CUST cust INNER JOIN BIZ_PTS pts ON pts.ROL = 'LETS' AND pts.OBJTYP = 'BIZ_SIGLE_PRODUCT_RULE' AND pts.PTYINR = cust.id_ INNER JOIN biz_sigle_product_rule pdt ON pts.OBJINR = pdt.ID_ AND pts.DEBT_CODE = pdt.DEBT_CODE INNER JOIN BIZ_PRODUCT_LINESTYPE ltype ON ltype.DEBT_CODE = pdt.DEBT_CODE AND ltype.CUST_NO = cust.CUST_NO INNER JOIN BIZ_CREDIT_LINES lines ON lines.DEBT_CODE = pdt.DEBT_CODE AND lines.OBJTYP = 'BIZ_DEBT_MAIN' AND lines.AMOUNT_TYPE = ltype.BIZ_CREDIT_LINES_ID AND lines.CUST_NO = ltype.CUST_NO
AND pdt.DEBT_CODE = #{cm.debtCode}
AND ltype.BUSINESS_TYPE = #{cm.businessTypes}

SELECT TO_CHAR (A .ID_) ID_, A .DEBT_CODE, A .PROJECT_NAME, A .BANK_TELL_ID, C.USER_NAME, C.DEPT_CODE, D .DEPT_NAME FROM BIZ_DEBT_MAIN A INNER JOIN SYS_USER C ON A .BANK_TELL_ID = C.id_ INNER JOIN SYS_DEPT D ON C.DEPT_CODE = D .ID_
and A.SOLUTION_STATE = #{cm.solutionState}
and (A.DEBT_CODE like '%'||#{cm.debtCode}||'%')

SELECT TO_CHAR(b.ID_) as TEMP_ID, a.CURRENCY, a.GRANT_AMT, a.SCOPE_BUSIN_PERIOD, a.PROPOSER, c.NAME_ as BNS_TYPE_NAME, b.BIZ_CODE FROM BIZ_DEBT_GRANT a INNER JOIN BIZ_TMPSAVE b ON a.GRANT_CODE = b.BIZ_CODE LEFT JOIN BIZ_PRODUCT_TYPES c ON a.BUSINESS_TYPES = c.CODE_
and b.TASK_ID = #{cm.taskid}
and b.ROL_ID = #{cm.rolid}
and b.USER_ID = #{cm.userid}
ORDER BY b.CREATE_TIME DESC

SELECT CASE WHEN SUBSTR(B.PARENT_CODE, 0,1)='1' THEN '1360' WHEN B.PARENT_CODE='2201000' THEN '3010' ELSE SUBSTR(B.PARENT_CODE,0,4) END PARENT_CODE FROM SYS_USER A INNER JOIN SYS_DEPT B ON A .DEPT_CODE = B.CODE_
1=1
and A.ID_ = #{cm.userId}
