mapper.select:
		select m.id_ from BIZ_DEBT_MAIN m
		LEFT JOIN
		biz_trn t
		ON (m.id_ = t.objinr
		and t.objtyp = 'BIZ_DEBT_MAIN'
        and m.vernum_ = t.vernum_
		)




                        and m.DEBT_CODE = SUBSTR(#{cm.debtCode},0,13)
                        and m.vernum_ = SUBSTR(#{cm.debtCode},16)


                        and m.DEBT_CODE = #{cm.debtCode}




                and m.VERNUM_ = #{cm.verNum}


				and m.BANK_TELL_ID = #{cm.bankTellerId}


				and t.BIZ_STATUS = #{cm.solutionState}


				and t.BIZ_STATUS in (6,10)


				and m.INSTITUTION_CODE = #{cm.institutionCode}


				and m.PROJECT_NAME = #{cm.projectName}


				and m.PROPOSER = #{cm.proposer}


				and m.M_CURRENCY = #{cm.mpc}


				and m.SOLUTION_AMT = #{cm.solutionAmount}


				and m.CREATE_TIME = #{cm.createTime}


				and m.enable_ = #{cm.enable}


				and m.PG_EFFECTIVE_DATE = #{cm.pgEffectivDate}


				and m.PG_EXPI_DATE = #{cm.pgExpiDate}


				and t.BIZ_STATUS <> #{cm.solutionState2}


				and t.BIZ_STATUS <> #{cm.solutionState7}


				and t.BIZ_STATUS <> #{cm.solutionState8}


				and (m.DEBT_CODE like '%'||#{cm.keyword}||'%')


				and (m.DEBT_CODE <> '%'||#{cm.debtCodeCon}||'%')


				and (m.DEBT_CODE like '%'||#{cm.schemeNum}||'%')


				and (m.PROJECT_NAME like '%'||#{cm.projectNam}||'%')


				and (m.PROPOSER like '%'||#{cm.applicant}||'%')


				and (m.INSTITUTION_CODE like '%'|| #{cm.deptCod}||'%')


				and (m.CREATE_TIME >= TO_DATE(#{cm.stateTime}, 'yyyy-mm-dd'))


				and (m.CREATE_TIME <= TO_DATE(#{cm.endTime}, 'yyyy-mm-dd'))


				and (m.PG_EFFECTIVE_DATE >= TO_DATE(#{cm.debtStartTime}||'00:00:00', 'yyyy-mm-dd hh24:mi:ss'))


				and (m.PG_EXPI_DATE  <=  TO_DATE(#{cm.debtEndTime}||'23:59:59', 'yyyy-mm-dd hh24:mi:ss'))


				and (m.CREATE_TIME >= TO_DATE(#{cm.startDate}||'00:00:00', 'yyyy-mm-dd hh24:mi:ss'))


				and (m.CREATE_TIME  <=  TO_DATE(#{cm.endDate}||'23:59:59', 'yyyy-mm-dd hh24:mi:ss'))

SELECT b.PG_EFFECTIVE_DATE, b.PG_EXPI_DATE, b.RULE_TYPE, b.LTNOPA, b.A_CURRENCY, b.M_CURRENCY, b.TDWLN, b.SOLUTION_AMT, b.SCOPE_BUSIN_PERIOD, b.LOOP_FLAG, AA.NUM_LIMIT, AA.DEAL_NUM, b.PACKAGE_RATE, b.SCHEME_RATE_FORM, b.RATE_RANGE_MIX, b.RATE_RANGE_MAX, ( SELECT COUNT(1) FROM BIZ_DEBT_GRANT c WHERE c.GRANT_STATUS!=6 and c.ENABLE_=1 and c.DEBT_CODE=#{cm.debtCode} ) as USED_NUM FROM BIZ_SIGLE_PRODUCT_RULE AA INNER JOIN BIZ_DEBT_MAIN b ON AA.DEBT_CODE = b.DEBT_CODE
AND AA.DEBT_CODE = #{cm.debtCode, jdbcType=VARCHAR}
AND AA.PROPER_INFO = #{cm.properInfo}

SELECT A .DEBT_CODE, A .PROJECT_NAME, A .FINANCE_PLATFORM AS financePlatform, B.GRANT_CODE, C.USER_NAME, D .DEPT_NAME, E .NAME_ AS productName, F.BIZ_RENTAL_FACTORING_CODE AS bizRentalFactoringCode, WM_CONCAT (H .NAME_) OVER (PARTITION BY G .GRANT_CODE) GUARANTEE_TYPE_TOTAL AS guaranteeTypeTotal, I.CUST_NO AS custNo, I.CUST_NAME_CN AS custCameCn, I.MAIN_BUSINESS AS mainBusiness, I.CUST_SCALE AS custScale, I.ORGANIZATION_CODE AS organizationCode, I.CREDIT_RATING AS creditRating, J.BUSINESS_TYPE AS businessType, J.INNOVATIVE_BUSINESS_TYPE AS innovativeBusinessType, J.BACKGROUND_NATIONALITY AS backgroundNationality, J.LOAN_DOMAIN AS loanDomain, J.IMPOERT_EXPORT_GOODS_SERVICE AS impoertExportGoodsService, -- J .CHINA_EMERGING_INDUSTRY_CLASSIFY, J.TYOES_INDUSTRIAL AS tyoesIndustrial, J.BUSINESS_CONTRACT_CODE AS businessContractCode, J.BUSINESS_CONTRACT_DATE AS businessContractDate, J.BUSINESS_CONTRACT_AMOUNT AS businessContractAmount, J.LOAN_CKASSIFICATION AS loanCkassification, J.INDUSTRIAL_TRANSFORMATION AS industrialTransformation, J.CURTURAL_PRODUCT AS curturalProduct, J.ALLEVIATION_LOAN AS alleviationLoan, J.TRADE_INTEREST_RATE AS tradeRnterestRate, -- J .POLICY_ATTRIBUTE_CLASSIFY, J.INDUSTRY_INVESTMENT AS industryInvestment, J.COMPARE AS compare, J.IS_SYNDICATED AS isSyndicated, J.IS_SYNDICATED_AGENCY AS isAyndicatedAgency, J.SYNDICATED_STATUS AS syndicatedStatus, J.IS_421 AS is421 FROM BIZ_DEBT_MAIN A INNER JOIN BIZ_DEBT_GRANT B ON A .DEBT_CODE = B.DEBT_CODE INNER JOIN SYS_USER C ON A .BANK_TELL_ID = C.id_ INNER JOIN SYS_DEPT D ON C.DEPT_CODE = D .ID_ INNER JOIN BIZ_PRODUCT_TYPES E ON B.BUSINESS_TYPES = E .CODE_ INNER JOIN BIZ_RENTAL_FACTORING F ON F.DEBT_CODE = B.DEBT_CODE AND F.GRANT_CODE = B.GRANT_CODE INNER JOIN BIZ_GUARANTEE_RESULT G ON G .GRANT_CODE = B.GRANT_CODE INNER JOIN BIZ_GUARANTEE_TYPE H ON G .GUARAN_TYPE = H .CODE_ INNER JOIN BIZ_CUSTOMER I ON I.DEBT_CODE = A .DEBT_CODE AND A .PROPOSER_NUM = I.CUST_NO INNER JOIN BIZ_INTEREST_RATE J ON J.DEBT_CODE = B.DEBT_CODE AND J.GRANT_CODE = B.GRANT_CODE
AND B.DEBT_CODE = #{cm.debtCode}
AND B.GRANT_CODE = #{cm.grantCode}
AND A.BUSINESS_TYPES = #{cm.businessTypes}

SELECT TO_CHAR (b.ID_) ID_, -- b.DEBT_CODE, b.DEBT_CODE || replace(lpad(to_char(b.vernum_),3),' ','0') as DEBT_CODE, b.PROJECT_NAME, b.PROPOSER, AA.DEPT_NAME, b.M_CURRENCY, b.SOLUTION_AMT, -- b.SOLUTION_STATE, dd.biz_status as SOLUTION_STATE, b.HISTORY_STATE, CC.USER_NAME, b.CREATE_TIME, b.UPDATE_TIME, b.PG_EXPI_DATE, b.vernum_ FROM BIZ_DEBT_MAIN b LEFT JOIN SYS_DEPT AA ON (b.INSTITUTION_CODE = AA.CODE_) LEFT JOIN SYS_USER CC ON (b.CREATE_BY = CC.ID_) LEFT JOIN biz_trn dd ON (b.id_ = dd.objinr and dd.objtyp='BIZ_DEBT_MAIN' and dd.vernum_=b.vernum_ )
AND (b.INSTITUTION_CODE like '%'||#{cm.deptCod}||'%')
AND (b.INSTITUTION_CODE like '%'||#{cm.institutionCode}||'%')
AND (b.DEBT_CODE like '%'||#{cm.schemeNum}||'%')
AND (b.PROJECT_NAME like '%'||#{cm.projectNam}||'%')
AND (b.PROPOSER like '%'||#{cm.applicant}||'%')
AND (AA.CODE_ like '%'||#{cm.deptCode}||'%')
AND (b.HISTORY_STATE like '%'||#{cm.historyState}||'%')
AND (b.UPDATE_TIME >= TO_DATE(#{cm.stateTime}||'00:00:00','yyyy-mm-dd hh24:mi:ss'))
AND (b.UPDATE_TIME <= TO_DATE(#{cm.endTime}||'23:59:59','yyyy-mm-dd hh24:mi:ss'))
AND (dd.biz_status like '%'||#{cm.solutionState}||'%')
AND dd.biz_status <> #{cm.solutionState2}
AND dd.biz_status <> #{cm.solutionState7}
AND dd.biz_status <> #{cm.solutionState8}
AND dd.RELFLG = #{cm.relflg}
AND dd.RELRES = #{cm.relres}
