mapper.select:
select id_ from BIZ_PRODUCT_TYPES


		and enable_ = #{cm.enable}


		and NAME_ = #{cm.name}


		and CODE_ = #{cm.code}


		and PARENT_CODE = #{cm.parentCode}


		and LEAF_ = #{cm.leaf}


		and (NAME_ like '%'||#{cm.keyword}||'%')

select distinct
product_types_code from BIZ_PRODUCT_TYPES_org where
dept_id=#{deptId}
